        //       Ontotal();
        //     }).catch((error) => {
        //       console.log(error);
        //     }).then(() => {
        //       axios
        //         .post('http://localhost:4000/user/xxx', Nuser)
        //         .then((response) => {
        //           // console.log(response.data);
        //           if (response.data.val == 1) {

        //             setTotal(response.data.wallet);

        //           }
        //         })
        //         .catch((error) => {
        //           console.log(error);
        //         }
        //         );
        //     })
        //   }

        // }
      })
  }, [])
  // settime(today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds())

const Addtofav = (name) =>{
  axios.post("http://localhost:4000/user/buyer_orders",{email:email,name:name}).then((res)=>{
    alert("added to favourites successfully");
    console.log(res.data)
  })
}
  console.log(searchInput);

  return (
    <div>
      <div> <Buyer_Navbar></Buyer_Navbar></div>
      {/* <div> <Wallet /> </div> */}
      <div>
        <h1><center> Wallet</center></h1>
        <div> <center>Wallet amount : {total}</center>
        </div>
        <div style={{ marginTop: 40 }} >
          <Grid container align={"center"} spacing={3}>

            <Grid item xs={12}>
              <TextField
                label="enter amount"
                variant="outlined"
                value={a}
                onChange={onChangeAmount}
              />
            </Grid>
            <Grid item xs={12}>
              <Button variant="contained" onClick={onSubmit}>
                add to wallet
              </Button>
            </Grid>
          </Grid>
        </div>
      </div>
      <Box>
        <div>
          <div style={{ backgroundColor: "#FAE03B", padding: 30, textAlign: "center", marginTop: 100, fontSize: 30 }}>
            <h1><center>Welcome {username}</center></h1>
            {/* <h1><center>email: {email}</center></h1>
        <h1><center>contactNumber: {contactNumber}</center></h1>
        <h1><center>age:{age}</center></h1>
        <h1><center>Batch: {batch}</center></h1> */}
          </div>
          <div style={{ marginTop: 30 }}>
            <h1 style={{ marginTop: 30, fontSize: 50, textAlign: "center", color: "orange" }}> FOOD </h1>
            <Grid container>
              <Grid item xs={12} md={3} lg={2}>
                <List component="nav" aria-label="mailbox folders">
                  <ListItem text>
                    <h1>Filters</h1>
                  </ListItem>
                </List>
              </Grid>
              <Grid item xs={12} md={9} lg={9}>
                <List component="nav" aria-label="mailbox folders">

                  <TextField
                    id="standard-basic"
                    label="Search"
                    fullWidth={true}
                    value={searchInput}
                    onChange={onChangeSearch}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment>
                          <IconButton  >
                            <SearchIcon />
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  // onChange={customFunction}
                  />

                </List>
              </Grid>
            </Grid>
            <Grid container>
              <Grid item xs={12} md={3} lg={2}>
                <List component="nav" aria-label="mailbox folders">
                  <ListItem>
                    <Grid container spacing={2}>
                      <Grid item xs={12}>
                        Salary
                      </Grid>
                      <Grid item xs={6}>
                        <TextField
                          id="standard-basic"
                          label="Enter Min"
                          fullWidth={true}
                        />
                      </Grid>
                      <Grid item xs={6}>
                        <TextField
                          id="standard-basic"
                          label="Enter Max"
                          fullWidth={true}
                        />
                      </Grid>
                    </Grid>
                  </ListItem>
                  <Divider />
