                                return (
                                  <Button variant="contained" color="error" onClick={() => {

                                  }}>
                                    Unavailable
                                  </Button>

                                )
                              }

                            })()}

                          </TableCell>
                          <TableCell><Button variant="contained" color="success" endIcon={<SendIcon />} onClick={() => {
                            {
                              AddtoSchema()
                            };
                            // { navigate("/myorders") }
                          }}>
                            order_now
                          </Button></TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </Paper>
              </Grid>
            </Grid>
          </div>
        </div>
      </Box>  </div>

  );
};
export default Buyer_Home;
